DELIMITER $$
CREATE TRIGGER `UTG_Report_Delete` BEFORE DELETE ON `bill` FOR EACH ROW BEGIN
	IF old.Status = 1 THEN
    	UPDATE REPORT
        SET REPORT.TotalPrice = REPORT.TotalPrice - old.TotalPrice
        WHERE DATE(REPORT._Date) = DATE(old.DateCheckIn);
    END IF;
END
$$
